import React from 'react'
import { useState } from 'react'
import { Link, withRouter, useHistory } from 'react-router-dom'
import * as auth from '../utils/auth'
import Header from './Header'
import InfoTooltip from './InfoTooltip'

function Login({ handleLogin }) {
  const [loginCredentials, setLoginCredentials] = useState({
    email: '',
    password: '',
  })
  const [isInfoTooltipOpen, setIsInfoTooltipOpenOpen] = useState(false)
  const [message, setMessage] = useState('')

  const { email, password } = loginCredentials

  let history = useHistory()

  function handleChange(e) {
    const { name, value } = e.target
    setLoginCredentials({ ...loginCredentials, [name]: value })
  }

  function handleSubmit(e) {
    e.preventDefault()
    if (!email || !password) {
      return
    }
    auth
      .authorize(email, password)
      .then((data) => {
        console.log(data)
        if (data.token) {
          setLoginCredentials({ email: '', password: '' })
          handleLogin()
          //show modal
          setIsInfoTooltipOpenOpen(true)
          //wait 3s and then redirect
          setTimeout(() => {
            history.push('/')
          }, 3000)
        }
      })
      .catch((err) => {
        if (err.statusCode === 400) {
          console.log('one or more of the fields were not provided')
        }
        if (err.statusCode === 401) {
          console.log('401 - the user with the specified email not found')
        }
        console.log(err)
      })
  }

  return (
    <div className='auth'>
      <Header email='' text='Log in' />
      <form autoComplete='off' onSubmit={handleSubmit} className='auth__form'>
        <h2 className='auth__form_title'>Log in</h2>
        <input
          autoComplete='new-password'
          className='auth__form_input'
          required
          placeholder='email'
          id='email'
          name='email'
          type='email'
          value={email}
          onChange={handleChange}
        />
        <input
          autoComplete='new-password'
          required
          className='auth__form_input'
          placeholder='password'
          id='password'
          name='password'
          type='password'
          value={password}
          onChange={handleChange}
        />
        <div className='auth__button-container'>
          <button
            type='submit'
            onSubmit={handleSubmit}
            className='auth__button'
          >
            Log in
          </button>
        </div>
      </form>

      <div className='auth__redirect'>
        <p>
          Not a member yet? Sign up{' '}
          <Link to='/signup' className='auth__redirect_link'>
            here
          </Link>
        </p>
      </div>
      <InfoTooltip isOpen={isInfoTooltipOpen} message={message} />
    </div>
  )
}

export default withRouter(Login)
