import { useState } from 'react'
import { Link, withRouter, useHistory } from 'react-router-dom'
import * as auth from '../utils/auth'
import Header from './Header'

function Register() {
  const [signinCredentials, setSigninCredentials] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: '',
  })
  const { username, email, password, confirmPassword } = signinCredentials

  let history = useHistory()

  const handleChange = (e) => {
    const { name, value } = e.target
    setSigninCredentials({ [name]: value })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    if (password === confirmPassword) {
      auth.register(username, password, email).then((res) => {
        if (res) {
          history.push('/login')
        } else {
          console.log('Something went wrong.')
        }
      })
    }
  }

  return (
    <div className='auth'>
      <div className='auth'>
        <Header email='' text='Sign up' />
        <form onSubmit={handleSubmit} className='auth__form'>
          <h2 className='auth__form_title'>Sign up</h2>
          <input
            className='auth__form_input'
            required
            placeholder='email'
            id='email'
            name='email'
            type='email'
            value={email}
            onChange={handleChange}
          />
          <input
            required
            className='auth__form_input'
            placeholder='password'
            id='password'
            name='password'
            type='password'
            value={password}
            onChange={handleChange}
          />
          <div className='auth__button-container'>
            <button
              type='submit'
              onSubmit={handleSubmit}
              className='auth__link'
            >
              Sign up{' '}
            </button>
          </div>
        </form>

        <div className='auth__signup'>
          <p>
            Already a member? Log in{' '}
            <Link to='/signin' className='signup__link'>
              here
            </Link>
          </p>
        </div>
      </div>
    </div>
  )
}

export default withRouter(Register)
