import { useEffect, useState } from 'react'
import Header from './Header'
import Main from './Main'
import Footer from './Footer'
import PopupWithForm from './PopupWithForm'
import ImagePopup from './ImagePopup'
import { Route, Switch, useHistory, withRouter } from 'react-router-dom'
import Login from './Login'
import Register from './Register'
import ProtectedRoute from './ProtectedRoute'
import * as auth from '../utils/auth'

function App() {
  let history = useHistory()

  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false)
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false)
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false)
  const [isImagePopupOpen, setIsImagePopupOpen] = useState(false)
  const [isInfoTooltipOpen, setIsInfoTooltipOpen] = useState(false)
  const [currentUser, setCurrentUser] = useState({})

  const [selectedCard, setSelectedCard] = useState({})
  const [loggedIn, setLoggedIn] = useState(false)
  const [email, setEmail] = useState('')

  useEffect(handleTokenCheck, [])

  useEffect(() => {
    api
      .getInitialCards()
      .then((values) => {
        setCards(values)
      })
      .catch((err) => {
        console.log(`can't get inital cards: ${err}`)
      })
  }, [])

  useEffect(() => {
    api
      .getUserInfo()
      .then((currentUser) => {
        setCurrentUser(currentUser)
      })
      .catch((err) => {
        console.log(`can't get inital user info: ${err}`)
      })
  }, [])

  function handleCardLike(card) {
    // Check one more time if this card was already liked
    const isLiked = card.likes.some((user) => user._id === currentUser._id)

    // Send a request to the API and getting the updated card data
    api
      .changeLikeCardStatus(card._id, !isLiked)
      .then(
        //api returns the new liked/unliked card object
        (newCard) => {
          console.log(newCard)
          setCards(
            // pass in a function to reliably get the value of the current state
            (state) =>
              state.map((currentCard) =>
                currentCard._id === card._id ? newCard : currentCard,
              ),
          )
        },
      )
      .catch((err) => {
        console.log(`can't chage like card status: ${err}`)
      })
  }

  function handleCardDelete(card) {
    // Send a request to the API and getting the updated card data
    api
      .deleteCard(card._id)
      .then(() => {
        setCards((state) =>
          state.filter((currentCard) => currentCard._id !== card._id),
        )
      })
      .catch((err) => {
        console.log(`can't delete card: ${err}`)
      })
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true)
  }
  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true)
  }
  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false)
    setIsAddPlacePopupOpen(false)
    setIsEditAvatarPopupOpen(false)
    setIsImagePopupOpen(false)
    setSelectedCard({})
  }

  function handleCardClick(card) {
    setSelectedCard(card)
    setIsImagePopupOpen(true)
  }

  function handleCloseAllPopups(e) {
    if (
      (e.type === 'click' &&
        (e.target.classList.contains('modal__close-button') ||
          e.target.classList.contains('modal_open'))) ||
      (e.type === 'keydown' && e.key === 'Escape')
    ) {
      closeAllPopups()
    }
  }
  function onLogin() {
    setLoggedIn(true)
  }
  function onLogout() {
    setLoggedIn(false)
  }

  function handleTokenCheck() {
    if (localStorage.getItem('jwt')) {
      const jwt = localStorage.getItem('jwt')
      // we're checking the user's token
      auth.checkToken(jwt).then((res) => {
        console.log(res)
        if (res) {
          setLoggedIn(true)
          history.push('/')
        }
      })
    }
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className='App'>
        <Switch>
          <Route path='/signup'>
            <Register />
          </Route>
          <Route path='/signin'>
            <Login handleLogin={onLogin} />
          </Route>
          <ProtectedRoute path='/' loggedIn={loggedIn}>
            <div className='page'>
              <Header email='' text='' />
              <Main
                onEditProfileClick={handleEditProfileClick}
                onAddPlaceClick={handleAddPlaceClick}
                onEditAvatarClick={handleEditAvatarClick}
                onCardClick={handleCardClick}
              />
              <Footer />
              <PopupWithForm
                name='edit-profile'
                title='Edit profile'
                isOpen={isEditProfilePopupOpen}
                onClose={handleCloseAllPopups}
                buttonText='Save'
              >
                <label className='modal__form-field'>
                  <input
                    name='name'
                    type='text'
                    className='modal__input'
                    id='name-input'
                    placeholder='Your name'
                    required
                    minLength='2'
                    maxLength='40'
                  />
                  <span className='modal__error name-input-error'></span>
                </label>

                <label className='modal__form-field'>
                  <input
                    name='about'
                    type='text'
                    className='modal__input'
                    id='about-input'
                    placeholder='About you'
                    required
                    minLength='2'
                    maxLength='200'
                  />
                  <span className='modal__error about-input-error'></span>
                </label>
              </PopupWithForm>
              <PopupWithForm
                name='add-card'
                title='New Place'
                isOpen={isAddPlacePopupOpen}
                onClose={handleCloseAllPopups}
                buttonText='Create'
              >
                <label className='modal__form-field'>
                  <input
                    name='title'
                    type='text'
                    className='modal__input'
                    id='title-input'
                    placeholder='Title of the new place'
                    required
                    minLength='1'
                    maxLength='30'
                  />
                  <span className='modal__error title-input-error'></span>
                </label>

                <label className='modal__form-field'>
                  <input
                    name='url'
                    type='url'
                    className='modal__input'
                    placeholder='Image link of the new place'
                    id='url-input'
                    required
                  />
                  <span className='modal__error url-input-error'></span>
                </label>
              </PopupWithForm>
              <PopupWithForm
                name='delete-card'
                title='Are you sure?'
                onClose={handleCloseAllPopups}
                buttonText='Save'
              >
                <button
                  type='submit'
                  aria-label='submit'
                  className='modal__submit-button'
                >
                  Yes
                </button>
              </PopupWithForm>
              <PopupWithForm
                name='update-avatar'
                title='Update profile picture'
                isOpen={isEditAvatarPopupOpen}
                onClose={handleCloseAllPopups}
                buttonText='Save'
              >
                <label className='modal__form-field'>
                  <input
                    name='url'
                    type='url'
                    className='modal__input'
                    placeholder='Image link of the new avatar'
                    id='link-input'
                    required
                  />
                  <span className='modal__error link-input-error'></span>
                </label>
              </PopupWithForm>
              <ImagePopup
                isOpen={isImagePopupOpen}
                onClose={handleCloseAllPopups}
                card={selectedCard}
              />
            </div>
          </ProtectedRoute>
        </Switch>
      </div>
    </CurrentUserContext.Provider>
  )
}

export default withRouter(App)
